# .tox/c1/bin/pytest --cov=weko_schema_ui tests/test_schema.py::test_SchemaConverter -vv --cov-branch --cov-report=term --basetemp=/code/modules/weko-schema-ui/.tox/c1/tmp
def test_SchemaConverter(app):
    with app.app_context():
        schema = "tests/data/collection.xsd"
        schema2 = "tests/data/collection2.xsd"
        schema3 = "tests/data/collection3.xsd"
        schema4 = "tests/data/collection4.xsd"
        rootname = "collection"
        with pytest.raises(Exception) as e:
            res = SchemaConverter(None, rootname)
        assert e.type==BadRequest
        assert str(e.value)=="400 Bad Request: Error creating Schema: Invalid schema file used"

        with pytest.raises(Exception) as e:
            res = SchemaConverter(schema, None)
        assert e.type==BadRequest
        assert str(e.value)=="400 Bad Request: Error creating Schema: Invalid root name used"

        with pytest.raises(Exception) as e:
            res = SchemaConverter(schema2, rootname)
            assert e.type==BadRequest
        assert str(e.value)=="400 Bad Request: Error creating Schema: No xsd element"

        with pytest.raises(Exception) as e:
            res = SchemaConverter(schema3, rootname)
            assert e.type==BadRequest
        assert str(e.value)=="'NoneType' object is not subscriptable"

        res = SchemaConverter(schema4, rootname)
        assert isinstance(res, SchemaConverter)
        assert res.namespaces == {'xml': 'http://www.w3.org/XML/1998/namespace', '': 'ddi:codebook:2_5', 'dc': 'http://purl.org/dc/terms/', 'fn': 'http://www.w3.org/2005/xpath-functions', 'saxon': 'http://xml.apache.org/xslt', 'xhtml': 'http://www.w3.org/1999/xhtml', 'xs': 'http://www.w3.org/2001/XMLSchema'}
        assert res.target_namespace == ''
        assert res.to_dict() == '{"collection:object": {"type": {"minOccurs": 1, "maxOccurs": "unbounded", "attributes": [{"name": "id", "ref": null, "use": "required"}, {"name": "available", "ref": null, "use": "required"}]}, "collection:position": {"type": {"minOccurs": 1, "maxOccurs": 1}}, "collection:title": {"type": {"minOccurs": 1, "maxOccurs": 1}}, "collection:year": {"type": {"minOccurs": 1, "maxOccurs": 1}}, "collection:author": {"type": {"minOccurs": 1, "maxOccurs": 1, "attributes": [{"name": "id", "ref": null, "use": "required"}]}, "collection:name": {"type": {"minOccurs": 1, "maxOccurs": 1}}, "collection:born": {"type": {"minOccurs": 1, "maxOccurs": 1}}, "collection:dead": {"type": {"minOccurs": 0, "maxOccurs": 1}}, "collection:qualification": {"type": {"minOccurs": 0, "maxOccurs": 1}}}, "collection:estimation": {"type": {"minOccurs": 0, "maxOccurs": 1}}, "collection:characters": {"type": {"minOccurs": 0, "maxOccurs": 1}, "collection:person": {"type": {"minOccurs": 1, "maxOccurs": "unbounded", "attributes": [{"name": "id", "ref": null, "use": "required"}]}, "collection:name": {"type": {"minOccurs": 1, "maxOccurs": 1}}, "collection:born": {"type": {"minOccurs": 1, "maxOccurs": 1}}, "collection:dead": {"type": {"minOccurs": 0, "maxOccurs": 1}}, "collection:qualification": {"type": {"minOccurs": 0, "maxOccurs": 1}}}}, "collection:age": {"type": {"minOccurs": 1, "maxOccurs": 1, "restriction": {"minInclusive": 0, "maxInclusive": 100}}}, "collection:initials": {"type": {"minOccurs": 1, "maxOccurs": 1, "restriction": {"patterns": ["^[A-Za-z][A-Za-z][A-Za-z]$"]}}}, "collection:car": {"type": {"minOccurs": 1, "maxOccurs": 1, "restriction": {"enumeration": ["Audi", "Golf", "BMW"]}}}}}'

        res = SchemaConverter(schema, rootname)
        assert isinstance(res, SchemaConverter)
        assert res.namespaces == {'xml': 'http://www.w3.org/XML/1998/namespace', 'xs': 'http://www.w3.org/2001/XMLSchema', '': 'http://example.com/ns/collection'}
        assert res.target_namespace == ''
        assert res.to_dict() == '{"object": {"type": {"minOccurs": 1, "maxOccurs": "unbounded", "attributes": [{"name": "id", "ref": null, "use": "required"}, {"name": "available", "ref": null, "use": "required"}, {"name": "xml-lang", "ref": null, "use": "required"}]}, "position": {"type": {"minOccurs": 1, "maxOccurs": 1}}, "title": {"type": {"minOccurs": 1, "maxOccurs": 1}}, "year": {"type": {"minOccurs": 1, "maxOccurs": 1}}, "author": {"type": {"minOccurs": 1, "maxOccurs": 1, "attributes": [{"name": "id", "ref": null, "use": "required"}]}, "name": {"type": {"minOccurs": 1, "maxOccurs": 1}}, "born": {"type": {"minOccurs": 1, "maxOccurs": 1}}, "dead": {"type": {"minOccurs": 0, "maxOccurs": 1}}, "qualification": {"type": {"minOccurs": 0, "maxOccurs": 1}}}, "estimation": {"type": {"minOccurs": 0, "maxOccurs": 1}}, "characters": {"type": {"minOccurs": 0, "maxOccurs": 1}, "collection:person": {"type": {"minOccurs": 1, "maxOccurs": "unbounded", "attributes": [{"name": "id", "ref": null, "use": "required"}]}, "name": {"type": {"minOccurs": 1, "maxOccurs": 1}}, "born": {"type": {"minOccurs": 1, "maxOccurs": 1}}, "dead": {"type": {"minOccurs": 0, "maxOccurs": 1}}, "qualification": {"type": {"minOccurs": 0, "maxOccurs": 1}}}}, "age": {"type": {"minOccurs": 1, "maxOccurs": 1, "restriction": {"minInclusive": 0, "maxInclusive": 100}}}, "initials": {"type": {"minOccurs": 1, "maxOccurs": 1, "restriction": {"patterns": ["^[A-Za-z][A-Za-z][A-Za-z]$"]}}}, "car": {"type": {"minOccurs": 1, "maxOccurs": 1, "restriction": {"enumeration": ["Audi", "Golf", "BMW"]}}}}, "content": {"type": {"minOccurs": 1, "maxOccurs": "unbounded"}}, "jeans_size": {"type": {"minOccurs": 1, "maxOccurs": 1, "restriction": {"enumeration": ["small", "medium", "large"]}}}}'
        
        schema5 = "tests/data/collection5.xsd"
        rootname = "order"
        cnv = SchemaConverter(schema, rootname)
        res = cnv.create_schema(schema5)
        assert res == (OrderedDict(), {'': 'http://example.com/ns/collection', 'xml': 'http://www.w3.org/XML/1998/namespace', 'xs': 'http://www.w3.org/2001/XMLSchema'}, '')
        
        schema = "tests/data/none.xsd"
        rootname = "collection"
        with pytest.raises(Exception) as e:
            res = SchemaConverter(schema, rootname)
        assert e.type==BadRequest
        
        
        schema = "tests/data/oai_dc.xsd"
        rootname = "dc"
        with pytest.raises(Exception) as e:
            res = SchemaConverter(schema, rootname)
        assert e.type==BadRequest
        

