      IDX_MIMETYPE="$(($RANDOM % $MAX_IDX_MIMETYPE))"
      IDX_INDEX_NAME="$(($RANDOM % $MAX_IDX_INDEX_NAME))"
      IDX_RESOURCE_TYPE="$(($RANDOM % $MAX_IDX_RESOURCE_TYPE))"
